            }
        });
    }

    public static void main(String[] args) {
        // Para testar, vamos pré-cadastrar um usuário
        try {
            Fachada.getInstace().adicionarUsuario(
                new Usuario("admin", "admin", "admin", "admin", "dica", enums.TipoUsuario.Administrador)
            );
